INSERT INTO `comments` (`id`, `story_id`, `content`, `user_id`, `created_at`, `deleted_at`) VALUES
(22, 11, 'Bình luận siêu dài nè hâu Liêm Nguyệt bỏ rất nhiều tâm sức để nâng đỡ một diễn viên vô danh tiểu tốt, khiến người ta khó mà hiểu nổi.\r\nHỏi anh vì sao.\r\nChẳng vì sao cả, chỉ cảm thấy sự cứng cỏi của cô rất thú vị.\r\nVì cái thú vị này, Châu Liêm Nguyệt lộ diện, chi tiền…\r\nCuối cùng, đến cả trái tim cũng dồn vào theo.\r\nKết quả lại phát hiện ra, cô diễn viên kia chẳng có chút tình cảm nào với anh cả.\r\nTrái tim của cô thuộc về ngư', 2, '2023-09-22 12:30:00', NULL),
(23, 11, '1212121', 2, '2023-09-22 12:41:05', NULL),
